INSERT INTO `#prefix#_bbcodes` (`bbcode_id`, `bbcode_tag`, `bbcode_helpline`, `display_on_posting`, `bbcode_match`, `bbcode_tpl`, `first_pass_match`, `first_pass_replace`, `second_pass_match`, `second_pass_replace`) VALUES
(1, 'youtube', 'Youtube video', 1, '[youtube]{SIMPLETEXT}[/youtube]', '<iframe width="560" height="315" src="https://www.youtube.com/embed/{SIMPLETEXT}?rel=0&amp;controls=0&amp;showinfo=0" frameborder="0" allow="autoplay; encrypted-media" allowfullscreen></iframe>', '!\\[youtube\\]([a-zA-Z0-9-+.,_ ]+)\\[/youtube\\]!i', '[youtube:$uid]${1}[/youtube:$uid]', '!\\[youtube:$uid\\]([a-zA-Z0-9-+.,_ ]+)\\[/youtube:$uid\\]!s', '<iframe width="560" height="315" src="https://www.youtube.com/embed/${1}?rel=0&amp;controls=0&amp;showinfo=0" frameborder="0" allow="autoplay; encrypted-media" allowfullscreen></iframe>'),
(2, 'highlight=', 'highlight', 1, '[highlight={COLOR}]{TEXT}[/highlight]', '<span style="background-color: {COLOR};">{TEXT}</span>', '!\\[highlight\\=([a-z]+|#[0-9abcdef]+)\\](.*?)\\[/highlight\\]!ies', '''[highlight=${1}:$uid]''.str_replace(array("\\r\\n", ''\\"'', ''\\'''', ''('', '')''), array("\\n", ''"'', ''&#39;'', ''&#40;'', ''&#41;''), trim(''${2}'')).''[/highlight:$uid]''', '!\\[highlight\\=([a-zA-Z]+|#[0-9abcdefABCDEF]+):$uid\\](.*?)\\[/highlight:$uid\\]!s', '<span style="background-color: ${1};">${2}</span>'),
(3, 'imageshackvid', 'ImageShack Video', 1, '[imageshackvid]{TEXT}[/imageshackvid]', '<embed src="{TEXT}" width="176" height="152" allowFullScreen="true" type="application/x-shockwave-flash"/>', '!\\[imageshackvid\\](.*?)\\[/imageshackvid\\]!ies', '''[imageshackvid]''.str_replace(array("\\r\\n", ''\\"'', ''\\'''', ''('', '')''), array("\\n", ''"'', ''&#39;'', ''&#40;'', ''&#41;''), trim(''$1'')).''[/imageshackvid]''', '!\\[imageshackvid\\](.*?)\\[/imageshackvid\\]!s', '<embed src="$1" width="176" height="152" allowFullScreen="true" type="application/x-shockwave-flash"/>'),
(4, 'spoiler', 'Spoiler', 1, '[spoiler]{TEXT}[/spoiler]', '<div class="shout"><div class="shout"><a href="#" onclick="n = this.parentNode.parentNode.lastChild; if(n.style.display == ''none'') { n.style.display = ''block''; } else {	n.style.display = ''none'';	} return false;">Spoiler (click to show/hide)</a></div><div class="spoiler" style="display: none;">{TEXT}</div></div>', '!\\[spoiler\\](.*?)\\[/spoiler\\]!ies', '''[spoiler:$uid]''.str_replace(array("\\r\\n", ''\\"'', ''\\'''', ''('', '')''), array("\\n", ''"'', ''&#39;'', ''&#40;'', ''&#41;''), trim(''${1}'')).''[/spoiler:$uid]''', '!\\[spoiler:$uid\\](.*?)\\[/spoiler:$uid\\]!s', '<div class="shout"><div class="shout"><a href="#" onclick="n = this.parentNode.parentNode.lastChild; if(n.style.display == ''none'') { n.style.display = ''block''; } else {	n.style.display = ''none'';	} return false;">Spoiler (click to show/hide)</a></div><div class="spoiler" style="display: none;">${1}</div></div>');
